package org.usfirst.frc157.FRC2016.commands;

import edu.wpi.first.wpilibj.command.Command;
import org.usfirst.frc157.FRC2016.Robot;

/**
 *
 */
public class PrintIMUOutput extends Command {

	private String text;
     public PrintIMUOutput(String textToPrint) {
    	
    	text = textToPrint;
  
    }

    // Called just before this Command runs the first time
    protected void initialize() {
    }

    // Called repeatedly when this Command is scheduled to run
    protected void execute() {
    	System.out.print(text);
    	System.out.print(" H:");
    	System.out.print(Robot.navigation.getHeading());
    	System.out.print(" P:");
    	System.out.print(Robot.navigation.getPitch());
    	System.out.print(" R:");
    	System.out.print(Robot.navigation.getRoll());
    	System.out.print(" Y:");
    	System.out.print(Robot.navigation.getYaw());
    	System.out.println("-");
    }

    // Make this return true when this Command no longer needs to run execute()
    protected boolean isFinished() {
        return true;
    }

    // Called once after isFinished returns true
    protected void end() {
    }

    // Called when another command which requires one or more of the same
    // subsystems is scheduled to run
    protected void interrupted() {
    }
}
